const headers=document.getElementsByClassName("header"),contents=document.getElementsByClassName("content"),icons=document.getElementsByClassName("icon");for(let n=0;n<headers.length;n++)headers[n].addEventListener("click",()=>{for(let e=0;e<contents.length;e++)n==e?(contents[e].style.display="block"==contents[e].style.display?"none":"block",icons[e].innerHTML="block"==contents[e].style.display?"&minus;":"&plus;"):(contents[e].style.display="none",icons[e].innerHTML="+")});